function getSubject(subject, ellipsisLimit)
{
    var output = subject;

    if (ellipsisLimit == undefined)
    {
        ellipsisLimit = 0;
    }

//	if (ellipsisLimit &gt; 0)
//	{
//	    output = Ext.util.Format.ellipsis(output, ellipsisLimit);
//    }

	 return output;
}
