        textarea_element += (g_BlockSendMail == true) ? &quot;disabled&quot; : &quot;&quot;;
        textarea_element += &quot;&gt;&quot; +defaultStr + &quot;&lt;/textarea&gt;&amp;nbsp;&amp;nbsp;&quot;;

